import React, { Component } from "react";
import axios from "axios";


class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toptext: "hello",
      bottomtext: "hello"
    }
  }

  changeText = (event) => {
    let id = event.target.id;
    if (id === "top-input") {
      this.setState({toptext : event.target.value});
    } else if (id === "bottom-input") {
      this.setState({bottomtext : event.target.value});
    }
  }

  fetchMemes = async (event) => {
    event.preventDefault();
    const memeAPI = "https://api.imgflip.com/get_memes";

    try {
      const response = await axios.get(`${memeAPI}`);
      const data = response.data;
      this.props.updateUI(data.data.meme);
    } catch (err) {
      console.log(err);
    }
  }

  generateMeme(images) {
    const image = images[Math.floor(Math.random() * images.length)]; 
  }

  render() {
    return (
      <section class="section">
        <div class="field has-addons">
          <div class="control is-expanded">
            <input
              class="input is-large is-fullwidth is-family-monospace"
              id="top-input"
              placeholder="Top text"
              type="text"
              value={this.state.toptext}
              onChange={this.changeText}
            />
          </div>
        </div>
        <div class="field has-addons">
          <div class="control is-expanded">
            <input
              class="input is-large is-fullwidth is-family-monospace"
              id="bottom-input"
              placeholder="Bottom text"
              type="text"
              value={this.state.bottomtext}
              onChange={this.changeText}
            />
          </div>
        </div>
        <div class="field has-addons">
          <div class="control is-expanded">
            <button 
              class="button is-link is-large is-fullwidth" 
              id="go-btn"
              onClick={this.fetchMemes}
            >
              Go!
            </button>
          </div>
        </div>
      </section>
    );
    }
}

export default Search;
